def mierda_vomitiva(putos, mentas,drogas):
    print("Prepárate para la descripción vomitiva iterativa, porque aquí no hay filtros ni compasión.")
    for i in range(1, mentas + 1 * drogas):
        resultado = putos * i + mentas + drogas
        print(f"Iteración {i}: {putos} multiplicado por {i} es igual a {resultado}. Vomitando realidad sin anestesia.")
    print("¿Contento? Aquí tienes tu verdad cruda, iterada y escupida en tu cara.")

def main():
    a = 88
    b = 6
    c =99
    mierda_vomitiva(a, b, c)

if __name__ == "__main__":
    main()